level=0
data={}
tfm.exec.disableAutoNewGame(true)
tfm.exec.disableAutoShaman(true)
tfm.exec.disableAutoTimeLeft(true)
tfm.exec.disableAfkDeath(true)
system.disableChatCommandDisplay("next")
level=level+1
for name,player in pairs(tfm.get.room.playerList) do
	tfm.exec.setPlayerScore(name,0,false)
		newData={
 		["v"]=5;
 		};
 		data[name] = newData;
end
function eventChatCommand(n,m)
	if m == "next" then
		if n == admin then
			runmap() end end end
function eventNewGame()
	tfm.exec.setUIMapName("Desafio do Haxhhhhhhhhh versão 1.0 - Level "..level.."")
	level=level+1
	runmap()
	for name,player in pairs(tfm.get.room.playerList) do
		if data[name].v <= 0 then
			tfm.exec.killPlayer(name)
		end
	end
end	
function eventLoop(p,f)
	for name,player in pairs(tfm.get.room.playerList) do
		if data[name].v == 1 then
			tfm.exec.displayParticle(5, 30, 30, 0.0, 0.0, 0.0, 0.0, name)
		elseif data[name].v == 2 then
			tfm.exec.displayParticle(5, 30, 30, 0.0, 0.0, 0.0, 0.0, name)
			tfm.exec.displayParticle(5, 60, 30, 0.0, 0.0, 0.0, 0.0, name)
		elseif data[name].v == 3 then
			tfm.exec.displayParticle(5, 30, 30, 0.0, 0.0, 0.0, 0.0, name)
			tfm.exec.displayParticle(5, 60, 30, 0.0, 0.0, 0.0, 0.0, name)
			tfm.exec.displayParticle(5, 90, 30, 0.0, 0.0, 0.0, 0.0, name)	
		elseif data[name].v == 4 then
			tfm.exec.displayParticle(5, 30, 30, 0.0, 0.0, 0.0, 0.0, name)
			tfm.exec.displayParticle(5, 60, 30, 0.0, 0.0, 0.0, 0.0, name)
			tfm.exec.displayParticle(5, 90, 30, 0.0, 0.0, 0.0, 0.0, name)	
			tfm.exec.displayParticle(5, 120, 30, 0.0, 0.0, 0.0, 0.0, name)
		elseif data[name].v == 5 then
			tfm.exec.displayParticle(5, 30, 30, 0.0, 0.0, 0.0, 0.0, name)
			tfm.exec.displayParticle(5, 60, 30, 0.0, 0.0, 0.0, 0.0, name)
			tfm.exec.displayParticle(5, 90, 30, 0.0, 0.0, 0.0, 0.0, name)	
			tfm.exec.displayParticle(5, 120, 30, 0.0, 0.0, 0.0, 0.0, name)
			tfm.exec.displayParticle(5, 150, 30, 0.0, 0.0, 0.0, 0.0, name)
		end
end
end
function eventNewPlayer(n)
		newData={
 		["v"]=0;
		};
 		data[n] = newData; end
function eventPlayerDied(n)
	data[n].v=data[n].v-1
	if data[n].v >= 1 then
		tfm.exec.respawnPlayer(n) end end
function runmap()
	if level == 1 then
		tfm.exec.newGame(mapa1)
	elseif level == 2 then
		tfm.exec.newGame(mapa2)
	elseif level == 3 then
		tfm.exec.newGame(mapa3)
